pub fn has_explicit_slur(handle: &str) -> bool {
    // Create a cleaned version of the handle with dots, hyphens and underscores removed
    let cleaned_handle = handle.replace(['.', '-', '_'], "");
    // Check both the original handle and the cleaned version
    EXPLICIT_SLUR_REGEXES
        .iter()
        .any(|regex| regex.is_match(handle) || regex.is_match(&cleaned_handle))
        || contains_explicit_slurs(handle)
        || contains_explicit_slurs(&cleaned_handle)
}
